// ProblemList.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProblemList = () => {
  const [problems, setProblems] = useState([]);

  // ※ 
  useEffect(() => {
    axios.get('/api/problems') // API 엔드포인트는 실제 서버 주소에 맞게 수정
      .then((res) => setProblems(res.data))   // 성공 시, 데이터를 state에 저장
      .catch((err) => console.error(err));    // 에러처리
  }, []);

  const tierText = (tier) => {
    const tiers = ["Unrated", "Bronze", "Silver", "Gold", "Platinum", "Diamond"];
    if (tier <= 0 || tier > 30) return "Unrated";
    const group = tiers[Math.floor((tier - 1) / 5) + 1];
    const level = 6 - ((tier - 1) % 5);
    return `${group} ${level}`;
  };

  return (
    <div>
      <h2>문제 리스트</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>제목</th>
            <th>티어</th>
            <th>푼 사람 수</th>
          </tr>
        </thead>
        <tbody>
          {problems.map((p) => (
            <tr key={p.problemId}>
              <td>{p.problemId}</td>
              <td>{p.titleKo}</td>
              <td>{tierText(p.tier)}</td>
              <td>{p.solvedCount.toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProblemList;
